// Card.js
import React from "react";
import styled from "styled-components";

const Card = ({ pokemon }) => {
  const { name, sprite, moves, abilities } = pokemon;

  return (
    <CardContainer className="card">
      <h2>{name}</h2>
      <img src={sprite} alt={name} />
      <Moves>
        <h4>Moves: {moves.join(", ")}</h4>
      </Moves>
      <Abilities>
        <h4>Abilities: {abilities.join(", ")}</h4>
      </Abilities>
    </CardContainer>
  );
};

const CardContainer = styled.div`
  /* Your styling for the card container */
`;

const Moves = styled.div`
  /* Your styling for moves */
`;

const Abilities = styled.div`
  /* Your styling for abilities */
`;

export default Card;
