import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import firebase from "../Firebase"; // Make sure the import path is correct
import "./NavStyle.css";

const Navbar = () => {
  const navigate = useNavigate();
  const auth = firebase.auth(); // Get the Firebase authentication instance

  const [searchInput, setSearchInput] = useState("");
  const [user, setUser] = useState(null); // Track the authenticated user

  const navigateToHome = () => {
    navigate("/");
  };

  const handleSearch = () => {
    if (searchInput.trim() !== "") {
      navigate(`/pokemon/${searchInput.trim().toLowerCase()}`);
    }
  };

  const handleKeyDown = (event) => {
    if (event.key === "Enter") {
      handleSearch();
    }
  };

  const handleLogin = async () => {
    try {
      // Replace with actual login logic using Firebase authentication
      const response = await auth.signInWithEmailAndPassword(email, password);
      setUser(response.user);
    } catch (error) {
      console.error('Login error:', error);
    }
  };

  const handleLogout = async () => {
    try {
      await auth.signOut();
      setUser(null);
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  return (
    <div className="navbar">
      <div className="nav-container">
        {/* ... Your search and title JSX ... */}

        <div className="right">
          <button className="nav-button" onClick={navigateToHome}>
            Home
          </button>
          {user ? (
            <div className="user-info">
              <p>Welcome, {user.email}</p>
              <button className="nav-button" onClick={handleLogout}>
                Logout
              </button>
            </div>
          ) : (
            <button className="nav-button" onClick={handleLogin}>
              Login
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Navbar;
