import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "./Navbar";
import Card from "./Card";
import "./homepage.css";

const HomePage = () => {
  const [pokemons, setPokemons] = useState([]); // Initialize an empty array for storing Pokémon data
  const [currentPage, setCurrentPage] = useState(1); // Initialize current page

  const fetchPokemonData = async (pokemonId) => {
    try {
      const response = await axios.get(`/api/pokemon/${pokemonId}`);
      return response.data;
    } catch (error) {
      console.error(error);
    }
  };

  const fetchPokemonsForPage = async (page) => {
    const startingPokemonId = (page - 1) * 4 + 1;
    const pokemonIds = Array.from({ length: 4 }, (_, index) => startingPokemonId + index);
    const fetchedPokemons = await Promise.all(
      pokemonIds.map((id) => fetchPokemonData(id))
    );
    setPokemons(fetchedPokemons);
  };

  const handleNextButtonClick = () => {
    const nextPage = currentPage + 1;
    fetchPokemonsForPage(nextPage);
    setCurrentPage(nextPage);
