import React from "react";
import styled from "styled-components";

const Card = (props) => {
  const { name, hp, moves, image_src } = props; // Destructuring the props
  return (
    <CardContainer className="card">
      <NameAndHP>
        <h2>{name}</h2>
        <h2>{hp}</h2>
      </NameAndHP>
      <ImageWrapper>
        <img src={image_src} alt={name} />
      </ImageWrapper>
      <Moves className="moves">
        {moves.map((move, index) => (
          <h4 key={index}>{move}</h4>
        ))}
      </Moves>
    </CardContainer>
  );
};

const CardContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
  width: 270px;
  height: 375px;
  padding: 1rem;
  border-radius: 10px;
  background-color: #1f1d1d;
  box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.35);
  color: white;
  font-family: "Arial", sans-serif;
  text-align: left; 
`;

const ImageWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: white;
  flex: 1;

  img {
    width: 150px;
    height: 150px;
  }
`;

const NameAndHP = styled.div`
  display: flex;
  justify-content: space-between;
  margin-right: 10px; 
  margin-left: 10px; 
`;

const Moves = styled.div`
  margin-left: 10px; 
`;

export default Card;
