import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "./Navbar";
import Card from "./Card";
import "./homepage.css";

const HomePage = () => {
  const [firstFourPokemonIds, setFirstFourPokemonIds] = useState([1, 2, 3, 4]);
  const [pokemons, setPokemons] = useState([]);
  const [searchInput, setSearchInput] = useState("");
  const [hasClickedNext, setHasClickedNext] = useState(false);

  const fetchPokemonData = async (pokemonId) => {
    try {
      const response = await axios.get(`/api/pokemon/${pokemonId}`);
      return response.data;
    } catch (error) {
      console.error(error);
    }
  };

  const fetchInitialPokemons = async (pokemonIds) => {
    const fetchedPokemons = await Promise.all(
      pokemonIds.map((id) => fetchPokemonData(id))
    );
    setPokemons(fetchedPokemons);
  };

  const handleNextButtonClick = () => {
    let updatedIds;
    if (hasClickedNext) {
      updatedIds = firstFourPokemonIds.map((id) =>
        id >= 1007 ? id - 1003 : id + 4
      );
    } else {
      updatedIds = firstFourPokemonIds.map((id) => id + 4);
    }
    fetchInitialPokemons(updatedIds);
    setFirstFourPokemonIds(updatedIds);
    setHasClickedNext(!hasClickedNext);
  };

  useEffect(() => {
    fetchInitialPokemons(firstFourPokemonIds);
  }, []);

  const searchPokemon = () => {
    if (searchInput.trim() !== "") {
      fetchPokemonData(searchInput.toLowerCase());
    }
  };

  useEffect(() => {
    searchPokemon();
    fetchInitialPokemons(firstFourPokemonIds);
  }, []);

  return (
    <div>
      <Navbar
        onSearch={searchPokemon}
        searchInput={searchInput}
        setSearchInput={setSearchInput}
      />
      <div className="card-grid">
        {pokemons.map((pokemon) => (
          <Card key={pokemon.id} pokemon={pokemon} />
        ))}
        <div className="button-container">
          <button className="next-button" onClick={handleNextButtonClick}>
            Next
          </button>
          <button className="next-button" onClick={handleBackButtonClick}>
            Back
          </button>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
