import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import Navbar from "./Navbar";
import { useNavigate } from "react-router-dom";
import "./Pokemon.css";

const Pokemon = () => {
  const { pokemonName } = useParams(); // Retrieve the route parameter
  const navigate = useNavigate();

  const [pokemon, setPokemon] = useState(null);
  const [searchInput, setSearchInput] = useState(""); // State to manage input value

  const fetchPokemonData = async (pokemonName) => {
    console.log("Fetching data for:", pokemonName); // Add this line
    try {
      const response = await axios.get(`/api/pokemon/${pokemonName}`);
      setPokemon(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchPokemonData(pokemonName); // Fetch Pokémon data based on the route parameter
  }, [pokemonName]);

  return (
    <>
      <Navbar />

      <div className="pokemon-details">
        {pokemon ? (
          <div className="pokemon-info">
            <h2>{pokemon.name}</h2>
            <img src={pokemon.sprite} alt={pokemon.name} />

            {/* Existing properties */}
            <p>Moves: {pokemon.moves.join(", ")}</p>
            <p>Abilities: {pokemon.abilities.join(", ")}</p>
            <p>Order: {pokemon.order}</p>
            <p>ID: {pokemon.id}</p>
            <p>Base Experience: {pokemon.baseExp}</p>
            <p>Forms: {pokemon.forms.map((form) => form.name).join(", ")}</p>
            <p>Height: {pokemon.height}</p>

            {/* Handle held_items */}
            <p>
              Held Items:{" "}
              {pokemon.held_items
                .map((item) =>
                  typeof item.item === "string" ? item.item : item.item.name
                )
                .join(", ")}
            </p>

            {/* Display Locations */}
            <p>Locations: {pokemon.locations}</p>

            {/* Handle stats */}
            <p>
              Stats:{" "}
              {pokemon.stats
                .map((stat) => `${stat.stat.name}: ${stat.base_stat}`)
                .join(", ")}
            </p>

            {/* Handle types */}
            <p>
              Types: {pokemon.types.map((type) => type.type.name).join(", ")}
            </p>

            {/* Handle color */}
            <p>Color: {pokemon.color.name}</p>

            {/* Handle evolutions */}

            {/* Display Capture Rate */}
            <p>Capture Rate: {pokemon.captureRate}</p>

            {/* Display Generation */}
            <p>Generation: {pokemon.generation.name}</p>

            {/* Display Habitat */}
            <p>Habitat: {pokemon.habitat.name}</p>
          </div>
        ) : (
          <div className="error-message">
            Please Enter a valid Pokemon name or ID!
          </div>
        )}
      </div>
    </>
  );
};

export default Pokemon;
