import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "./Navbar";
import Card from "./Card"; // Make sure the filename matches the actual file name

const HomePage = () => {
  const [pokemonList, setPokemonList] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  const fetchPokemonList = async (page) => {
    try {
      const response = await axios.get(`/api/pokemon-list?page=${page}`); // Replace with the actual endpoint to fetch a list of Pokémon
      setPokemonList(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleNextPage = () => {
    setCurrentPage((prevPage) => prevPage + 1);
  };

  useEffect(() => {
    fetchPokemonList(currentPage);
  }, [currentPage]);

  return (
    <div>
      <Navbar />
      <div className="card-grid">
        {pokemonList.map((pokemon, index) => (
          <Card key={index} pokemon={pokemon} />
        ))}
      </div>
      <button onClick={handleNextPage}>Next</button>
    </div>
  );
};

export default HomePage;
