import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "./Navbar";
import Card from "./Card";
import "./homepage.css";

const HomePage = () => {
  const [pokemons, setPokemons] = useState([]); // Initialize an empty array for storing Pokémon data
  const [searchInput, setSearchInput] = useState("");

  const fetchPokemonData = async (pokemonId) => {
    // Change the parameter name to `pokemonId`
    try {
      const response = await axios.get(`/api/pokemon/${pokemonId}`);
      return response.data; // Return the fetched Pokémon data
    } catch (error) {
      console.error(error);
    }
  };

  const fetchInitialPokemons = async () => {
    const firstFourPokemonIds = [1, 2, 3, 4]; // IDs of the first four Pokémon
    const fetchedPokemons = await Promise.all(
      firstFourPokemonIds.map((id) => fetchPokemonData(id))
    );
    setPokemons(fetchedPokemons); // Store the fetched Pokémon data in the 'pokemons' state array
  };

  useEffect(() => {
    fetchInitialPokemons(); // Fetch and store data for the initial four Pokémon
  }, []);

  const searchPokemon = () => {
    if (searchInput.trim() !== "") {
      fetchPokemonData(searchInput.toLowerCase());
    }
  };

  useEffect(() => {
    searchPokemon();
    fetchInitialPokemons(); // Fetch and store data for the initial four Pokémon
  }, []);

  return (
    <div>
      <Navbar
        onSearch={searchPokemon}
        searchInput={searchInput}
        setSearchInput={setSearchInput}
      />
      <div className="card-grid">
        {<Card pokemon={pokemon} />}
        {<Card pokemon={pokemon} />}
        {<Card pokemon={pokemon} />}
        {<Card pokemon={pokemon} />}

      </div>
    </div>
  );
};

export default HomePage;
