import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "./Navbar";
import Card from "./card"; // Make sure the filename matches the actual file name

const HomePage = () => {
  const [pokemonList, setPokemonList] = useState([]);

  const fetchPokemonList = async () => {
    try {
      const response = await axios.get(`/api/pokemon-list`); // Replace with the actual endpoint to fetch a list of Pokémon
      setPokemonList(response.data.slice(0, 4)); // Take the first 4 Pokémon from the list
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchPokemonList();
  }, []);

  return (
    <div>
      <Navbar />
      <div className="card-grid">
        {pokemonList.map((pokemon, index) => (
          <Card key={index} pokemon={pokemon} />
        ))}
      </div>
    </div>
  );
};

export default HomePage;
