import React from "react";
import styled from "styled-components";

const Card = ({ pokemon, onClick }) => {
  const { name, id, sprite, moves, abilities } = pokemon;

  return (
    <CardContainer className="card">
      <h2>{name.charAt(0).toUpperCase() + name.slice(1)}</h2>
      <h2>ID: {id}</h2>
      <ImageWrapper>
        <img src={sprite} alt={name} />
      </ImageWrapper>
      <Moves>
        <h4>Moves:</h4>
        {moves.map((move, index) => (
          <MoveItem key={index}>{move}</MoveItem>
        ))}
      </Moves>
      <Abilities>
        <h4>Abilities:</h4>
        {abilities.map((ability, index) => (
          <AbilityItem key={index}>{ability}</AbilityItem>
        ))}
      </Abilities>
    </CardContainer>
  );
};

const CardContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
  width: 16.7em;
  height: 30em;
  padding: 1rem;
  border-radius: 10px;
  background-color: #1f1d1d;
  box-shadow: 0px 5px 15px rgba(0, 0, 0, 0.35);
  color: white;
  font-family: "Arial", sans-serif;
  text-align: left;

  /* Align name to the left and ID to the right */
  h2 {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
`;

const ImageWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: white;
  flex: 1;

  img {
    width: 150px;
    height: 150px;
  }
`;

const Moves = styled.div`
  margin-left: 10px;
`;

const MoveItem = styled.p`
  margin: 5px 0;
`;

const Abilities = styled.div`
  margin-left: 10px;
`;

const AbilityItem = styled.p`
  margin: 5px 0;
`;

export default Card;
