const Pokemon = () => {
  const { pokemonName } = useParams();
  const navigate = useNavigate();

  const [pokemon, setPokemon] = useState(null);

  const fetchPokemonData = async (pokemonName) => {
    try {
      const response = await axios.get(`/api/pokemon/${pokemonName}`);
      setPokemon(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetchPokemonData(pokemonName);
  }, [pokemonName]);

  return (
    <>
      <Navbar />

      <div className="pokemon-details">
        {pokemon ? (
          <div className="pokemon-info">
            <h2>{pokemon.name}</h2>
            <img src={pokemon.sprite} alt={pokemon.name} />
            <div>
              {/* Dynamically display all properties */}
              {Object.keys(pokemon).map((key) => (
                <p key={key}>
                  <strong>{key}: </strong>
                  {typeof pokemon[key] === "object"
                    ? JSON.stringify(pokemon[key])
                    : pokemon[key]}
                </p>
              ))}
            </div>
          </div>
        ) : (
          <div className="error-message">
            Please Enter a valid Pokemon name or ID!
          </div>
        )}
      </div>
    </>
  );
};

export default Pokemon;
